#include <iomanip>void drawMenu(){    printf("Options:\n1 - addition\n2 - subtraction\n3 - multiplication\n4 - division\n5 - toggle calculator type\n6 - exit program\nPlease enter your option:");}int main(){    bool intMode = true;    while (true){        drawMenu();        int inputValue;        scanf(" %d", &inputValue);        switch (inputValue){        case 1:        {                        if (intMode){                int inputValue1;                printf("Enter First Term: ");                scanf(" %d", &inputValue1);                int inputValue2;                printf("Enter Second Term: ");                scanf(" %d", &inputValue2);                printf("The sum is: %d\n", inputValue1+inputValue2);            }            else {                double inputValue1;                printf("Enter First Term: ");                scanf(" %f", &inputValue1);                double inputValue2;                printf("Enter Second Term: ");                scanf(" %f", &inputValue2);                printf("The sum is: %f\n", inputValue1+inputValue2);            }            break;        }        case 2:        {                        if (intMode){                int inputValue1;                printf("Enter First Term: ");                scanf(" %d", &inputValue1);                int inputValue2;                printf("Enter Second Term: ");                scanf(" %d", &inputValue2);                printf("The difference is: %d\n", inputValue1-inputValue2);            }            else {                double inputValue1;                printf("Enter First Term: ");                scanf(" %f", &inputValue1);                double inputValue2;                printf("Enter Second Term: ");                scanf(" %f", &inputValue2);                printf("The difference is: %f\n", inputValue1-inputValue2);            }            break;        }        case 3:        {                        if (intMode){                int inputValue1;                printf("Enter First Term: ");                scanf(" %d", &inputValue1);                int inputValue2;                printf("Enter Second Term: ");                scanf(" %d", &inputValue2);                printf("The product is: %d\n", inputValue1*inputValue2);            }            else {                double inputValue1;                printf("Enter First Term: ");                scanf(" %f", &inputValue1);                double inputValue2;                printf("Enter Second Term: ");                scanf(" %f", &inputValue2);                printf("The product is: %f\n", inputValue1*inputValue2);            }            break;        }        case 4:        {                        if (intMode){                int inputValue1;                printf("Enter First Term: ");                scanf(" %d", &inputValue1);                int inputValue2;                printf("Enter Second Term: ");                scanf(" %d", &inputValue2);                printf("The quotient is: %d\n", inputValue1-inputValue2);            }            else {                double inputValue1;                printf("Enter First Term: ");                scanf(" %f", &inputValue1);                double inputValue2;                printf("Enter Second Term: ");                scanf(" %f", &inputValue2);                printf("The quotient is: %f\n", inputValue1-inputValue2);            }            break;        }        case 5:        {            intMode = !intMode;            if (intMode)                printf("Switching to int\n");            else                 printf("Switching to double\n");            break;        }        case 6:        {            exit(0);            break;        }        default:        {            printf("Invalid Command try again\n");        }            }    }    return 0;}