#include <iostream>#include <string>using namespace std;bool isPalindrome(std::string wordToCheck ){    int j;    // declare the length of the word to check    int len = wordToCheck.length();    // loop though half of the word         for(int i=0, j=len-1; i<len/2; ++i,--j)        {            // if the front half of the word doesnt match the second half, return false            if(wordToCheck[j]!=wordToCheck[i])                return false;        }        return true;}int main(){    std::string word1;    word1 = "momm";    bool testvar = isPalindrome(word1);    std::cout << testvar;    return 0;}